    /// Default sounds bundled with the app (auto-loaded from bundle .mp3 files)
    static let defaultSounds: [String] = {
        guard let resourceURL = Bundle.main.resourceURL else { return [] }
        let fileManager = FileManager.default
        guard let enumerator = fileManager.enumerator(at: resourceURL, includingPropertiesForKeys: nil) else { return [] }
        var names: [String] = []
        for case let fileURL as URL in enumerator {
            if fileURL.pathExtension.lowercased() == "mp3" {
                names.append(fileURL.deletingPathExtension().lastPathComponent)
            }
        }
        return names
    }()

